function [error_log_train, error_log_test, error_zero_one_train, error_zero_one_test, W] = learn_SGD()  #load train data  [X_train, Y_train] = readdata("data/file21_train.txt", " ");    #load test data  [X_test, Y_test] = readdata("data/file21_test.txt", " ");    #get W vector  tic  [W, W_epoch] = reg_log_SGD(X_train, Y_train);  toc  csvwrite('results/W_SGD.txt', W);    #add additional ones vector to the begining of X matrix  n = size(X_train, 1);  X_train = [ones(n, 1), X_train];  n = size(X_test, 1);  X_test = [ones(n, 1), X_test];    #predict  Y_pred_train = X_train*W;  Y_pred_test = X_test*W;    #count log error  error_log_train = mean(log(1 + e.^((-1).*Y_pred_train.*Y_train)))  error_log_test = mean(log(1 + e.^((-1).*Y_pred_test.*Y_test)))    #count 0/1 error  error_zero_one_train = mean(Y_pred_train.*Y_train <= 0)  error_zero_one_test = mean(Y_pred_test.*Y_test <= 0)    error_log_epoch = [];  error_zero_one_epoch = [];    #count log error and 0/1 error after every epoch  epoch_num = size(W_epoch, 2);  for i=1:epoch_num    Y_pred = X_train*W_epoch(:, i);    error_log_epoch = [error_log_epoch, mean(log(1 + e.^((-1).*Y_pred.*Y_train)))];    error_zero_one_epoch = [error_zero_one_epoch, mean(Y_pred.*Y_train <= 0)];  end    #plot things  f = figure();  plot(1:epoch_num, error_log_epoch, "-r;SGD;",    1:epoch_num, 0.33518*ones(epoch_num, 1)', "--b;N-R;");  title('Zaleznosc bledu logisitycznego od epok');  xlabel('epoka');  ylabel('wartosc bledu');  axis( [0 epoch_num 0 max(error_log_epoch)+0.05] );  saveas(f, 'results/plot1SGD.png');    f = figure();  plot(1:epoch_num, error_zero_one_epoch, "-r;SGD;",    1:epoch_num, 0.14908*ones(epoch_num, 1)', "--b;N-R;");  title('Zaleznosc bledu 0/1 od epok');  xlabel('epoka');  ylabel('wartosc bledu');  axis( [0 epoch_num 0 max(error_zero_one_epoch)+0.05] );  saveas(f, 'results/plot2SGD.png');    f = figure();  hist(W);  title('Histogram wag dla metody SGD');  saveas(f, 'results/histSGD.png');end